var getImages=function(a){var b,c;b=[];a=$(".UFICommentContentBlock").find('a[rel="theater"]');0<a.length&&a.each(function(){c=getDownloader($(this).attr("href"));b.push(c)});return b},getDownloader=function(a){var b;b=null;$.ajax({url:a,type:"get",dataType:"html",async:!1,success:function(a){b=a}});a=$.parseHTML(b);return $(".fbPhotoImage",a).attr("src")};chrome.extension.sendMessage({action:"getImages",source:getImages(document)});